import groovy.json.*

if( context.loopIndex == null )
context.loopIndex = 0
def numberOfTimes = context.expand( '${#TestCase#numberOfTimes}' ).toInteger()

def expectedValue = context.expand( '${Update Filter Criteria By CustomerKey#RawRequest#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:UpdateRequest[1]/ns1:Objects[1]/ns1:DataFilter[1]/ns1:Value[1]}' )
def actualValue = context.expand( '${Retrieve FilterDef To Confirm Update#Response#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:RetrieveResponseMsg[1]/ns1:Results[1]/ns1:DataFilter[1]/ns1:LeftOperand[1]/ns1:Value[1]}' )


if ( expectedValue != actualValue ) {
  if ( ++context.loopIndex <  numberOfTimes ) {
    log.info "Filter not updated yet"
    sleep(1000)
      testRunner.gotoStepByName( "Retrieve FilterDef To Confirm Update" )
  }
  else {
    log.info "Timeout, this test should fail"
    testRunner.fail("Filter not updated after " + numberOfTimes.toString() + " retrieve attempts, timing out")    
  }
}
else {
  log.info "Filter updated"
    testRunner.gotoStepByName( "Delete FilterDef" ) 
}
