import groovy.util.XmlSlurper

def response = new XmlSlurper().parseText(context.expand( '${Retrieve EmailSendDefinition to Confirm Create#Response#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:RetrieveResponseMsg[1]}' ))

if( context.loopIndex == null )
context.loopIndex = 0
def numberOfTimes = context.expand( '${#TestCase#numberOfTimes}' ).toInteger()
if ( response.Results.Name != context.expand( '${Groovy Script to Create Object Name#result}' ) ) {
  if ( ++context.loopIndex <  numberOfTimes ) {
    log.info "EmailSendDefinition not created yet"
    sleep(1000)
    testRunner.gotoStepByName( "Retrieve EmailSendDefinition to Confirm Create" )
  }
  else {
    log.info "Timeout, test should fail"
    testRunner.fail("EmailSendDefinition not created after " + numberOfTimes.toString() + " retrieve attempts, timing out")
  }
}
else {
  log.info "EmailSendDefinition created"
  testRunner.gotoStepByName( "Transfer ObjectID" ) 
}

