con:configuration.scriptText:
import groovy.util.XmlSlurper

def response = context.expand( '${Retrieve DE to Confirm Rowset Added#Response#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:RetrieveResponseMsg[1]}' )

def baseNode  = new XmlSlurper().parseText(response)

assert 20 == baseNode.depthFirst().findAll { it.name() == 'Results' }.size()

con:config.script:
import groovy.util.XmlSlurper

if( context.loopIndex == null )
context.loopIndex = 0
def numberOfTimes = context.expand( '${#TestCase#numberOfTimes}' ).toInteger()

def response = context.expand( '${Retrieve DE to Confirm Rowset Added#Response#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:RetrieveResponseMsg[1]}' )

def baseNode  = new XmlSlurper().parseText(response)

def DEName = context.expand( '${createCustomObject#RawRequest#$.name}' )


if (!20 == baseNode.depthFirst().findAll { it.name() == 'Results' }.size()) {
  if ( ++context.loopIndex <  numberOfTimes ) {
    log.info "DE rowset not added yet"
    sleep(1000)
      testRunner.gotoStepByName( "Retrieve DE to Confirm Rowset Added" )
  }
  else {
    log.info "Timeout, this test should fail"
    testRunner.fail("DE rowset not added after " + numberOfTimes.toString() + " retrieve attempts, timing out")
  }
}
else {
  //log.info "DE rowset added"
    testRunner.gotoStepByName( "Async Delete Rowset" ) 
}

