con:config.script:
def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}

generator( (('A'..'Z')+('0'..'9')).join(), 9 ) + "_QETesting"

con:configuration.scriptText:
def expectedName = context.expand( '${Create Filter Definition on Gender#RawRequest#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:CreateRequest[1]/ns1:Objects[1]/ns1:Name[1]}' )
def actualName = context.expand( '${Retrieve FilterDef by ObjectID#Response#declare namespace ns1=\'http://exacttarget.com/wsdl/partnerAPI\'; //ns1:RetrieveResponseMsg[1]/ns1:Results[1]/ns1:Name[1]}' )

assert expectedName == actualName

